INSERT INTO ART_discussions SET issue_id = "86172211160044268001323532628",
title = "Q1: Should there be encouragement or guidelines for contractual arrangements between right holders and users for the implementation of copyright exceptions?";
INSERT INTO ART_discussions SET issue_id = "86172211160424414001323532704",
title = "Q2: Should there be encouragement, guidelines or model licenses for contractual arrangements between right holders and users on other aspects not covered by copyright exceptions?";
INSERT INTO ART_discussions SET issue_id = "86172211160571292001323532770",
title = "Q3: Is an approach based on a list of non-mandatory exceptions adequate in the light of evolving Internet technologies and the prevalent economic and social expectations?";
INSERT INTO ART_discussions SET issue_id = "86172211160004862001323532811",
title = "Q4: Should certain categories of exceptions be made mandatory to ensure more legal certainty and better protection of beneficiaries of exceptions?";
INSERT INTO ART_discussions SET issue_id = "86172211160274349001323532960",
title = "Q5: If so, which ones?";
INSERT INTO ART_discussions SET issue_id = "86172211160338255001323533551",
title = "Q6: Should the exception for libraries and archives remain unchanged because publishers themselves will develop online access to their catalogues?";
INSERT INTO ART_discussions SET issue_id = "86172211160538262001323533595",
title = "Q7: In order to increase access to works, should publicly accessible libraries, educational establishments, museums and archives enter into licensing schemes with the publishers? Are there examples of successful licensing schemes for online access to library collections?";
INSERT INTO ART_discussions SET issue_id = "86172211160684959001323533681",
title = "Q8: Should the scope of the exception for publicly accessible libraries, educational establishments, museums and archives be clarified with respect to: format shifting; The number of copies that can be made under the exception; The scanning of entire collections held by libraries?";
INSERT INTO ART_discussions SET issue_id = "86172211160319722001323533736",
title = "Q9: Should the law be clarified with respect to whether the scanning of works held in libraries for the purpose of making their content searchable on the Internet goes beyond the scope of current exceptions to copyright?";
INSERT INTO ART_discussions SET issue_id = "86172211160975727001323533778",
title = "Q10: Is a further Community statutory instrument required to deal with the problem of orphan works, which goes beyond the Commission Recommendation 2006/585/EC of 24 August 2006?";
INSERT INTO ART_discussions SET issue_id = "86172211160577778001323533828",
title = "Q11: If so, should this be done by amending the 2001 Directive on Copyright in the information society or through a stand-alone instrument?";
INSERT INTO ART_discussions SET issue_id = "86172211160716819001323533881",
title = "Q12: How should the cross-border aspects of the orphan works issue be tackled to ensure EU-wide recognition of the solutions adopted in different Member States?";
INSERT INTO ART_discussions SET issue_id = "86172211160984157001323534441",
title = "Q13: Should people with a disability enter into licensing schemes with the publishers in order to increase their access to works? If so, what types of licensing would be most suitable? Are there already licensing schemes in place to increase access to works for the disabled people?";
INSERT INTO ART_discussions SET issue_id = "86172211160042978001323534488",
title = "Q14: Should there be mandatory provisions that works are made available to people with a disability in a particular format?";
INSERT INTO ART_discussions SET issue_id = "86172211160499399001323534556",
title = "Q15: Should there be a clarification that the current exception benefiting people with a disability applies to disabilities other than visual and hearing disabilities?";
INSERT INTO ART_discussions SET issue_id = "86172211160900082001323534625",
title = "Q16: If so, which other disabilities should be included as relevant for online dissemination of knowledge?";
INSERT INTO ART_discussions SET issue_id = "86172211160332931001323534674",
title = "Q17: Should national laws clarify that beneficiaries of the exception for people with a disability should not be required to pay remuneration for using a work in order to convert it into an accessible format?";
INSERT INTO ART_discussions SET issue_id = "86172211160191863001323534726",
title = "Q18: Should Directive 96/9/EC on the legal protection of databases have a specific exception in favour of people with a disability that would apply to both original and sui generis databases?";
INSERT INTO ART_discussions SET issue_id = "86172211160427606001323535267",
title = "Q19: Should the scientific and research community enter into licensing schemes with publishers in order to increase access to works for teaching or research purposes? Are there examples of successful licensing schemes enabling online use of works for teaching or research purposes?";
INSERT INTO ART_discussions SET issue_id = "86172211160173963001323535315",
title = "Q20: Should the teaching and research exception be clarified so as to accommodate modern forms of distance learning?";
INSERT INTO ART_discussions SET issue_id = "86172211160893172001323535364",
title = "Q21: Should there be a clarification that the teaching and research exception covers not only material used in classrooms or educational facilities, but also use of works at home for study?";
INSERT INTO ART_discussions SET issue_id = "86172211160891869001323535412",
title = "Q22: Should there be mandatory minimum rules as to the length of the excerpts from works which can be reproduced or made available for teaching and research purposes?";
INSERT INTO ART_discussions SET issue_id = "86172211160075286001323535460",
title = "Q23: Should there be a mandatory minimum requirement that the exception covers both teaching and research?";
INSERT INTO ART_discussions SET issue_id = "86172211160628025001323537143",
title = "Q24: Should there be more precise rules regarding what acts end users can or cannot do when making use of materials protected by copyright?";
INSERT INTO ART_discussions SET issue_id = "86172211160349383001323537186",
title = "Q25: Should an exception for user-created content be introduced into the Directive?";
INSERT INTO ART_discussions SET issue_id = "86172211160967585001323537454",
title = "Q: Any other comments?";
